sp_angular_provider.script:
function BDSTEPUtil($http){
	this.getFieldName = function(sessionId){
		return new Promise(function(resolve, reject){
			$http({
				method : "GET",
				url : "/api/x_naci_bd_step/fellowship/getFieldName",
						beforeSend: function(xhr){xhr.setRequestHeader('X-UserToken', sessionId);},
						headers: {'Content-Type': 'application/json'}
			}).then(resolve, reject);
		});
	};
	this.getSiteName = function(sessionId){
		return new Promise(function(resolve, reject){
			$http({
				method : "GET",
				url : "/api/x_naci_bd_step/fellowship/getSiteName",
				beforeSend: function(xhr){xhr.setRequestHeader('X-UserToken', sessionId);},
				headers: {'Content-Type': 'application/json'}
			}).then(resolve, reject);	
		});	
	};
	this.submitApplication = function(data, sessionId){
		return new Promise(function(resolve, reject){
			$http({
				method: "POST",
				url : "/api/x_naci_bd_step/fellowship/submitApplication",
				beforeSend: function(xhr){xhr.setRequestHeader('X-UserToken', sessionId);},
				headers: {'Content-Type': 'application/json'},
				data: {data: data}
			}).then(resolve, reject);
		});
	};
}
